import axios from 'axios';

// Change baseURL from '/api/v1' to '/api' to match your Spring Boot configuration
const api = axios.create({
  baseURL: '/api', // Removed /v1 to match your server.servlet.context-path=/api
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json',
  },
});

api.interceptors.request.use(
  (config) => {
    console.log(`ðŸš€ ${config.method?.toUpperCase()} ${config.url}`, config.data || config.params);
    return config;
  },
  (error) => {
    console.error('âŒ Request Error:', error);
    return Promise.reject(error);
  }
);

api.interceptors.response.use(
  (response) => {
    console.log(`âœ… ${response.config.method?.toUpperCase()} ${response.config.url}`, response.data);
    return response;
  },
  (error) => {
    console.error('âŒ Response Error:', error.response?.data || error.message);
    if (error.response?.status === 404) {
      throw new Error('Resource not found');
    } else if (error.response?.status === 400) {
      throw new Error(error.response.data.message || 'Invalid request');
    } else if (error.response?.status === 500) {
      throw new Error('Server error occurred');
    } else if (error.code === 'ECONNABORTED') {
      throw new Error('Request timeout - please try again');
    }
    throw error;
  }
);

export const apiService = {
  // Existing functions...
  createSpill: (spillData) => api.post('/spills', spillData),
  calculateDispersion: (spillId) => api.post(`/dispersion/spills/${spillId}/calculate`),
  getActiveSpills: () => api.get('/dispersion/spills'),
  getSpillById: (id) => api.get(`/spills/${id}`),
  updateSpillStatus: (id, status) => api.put(`/spills/${id}/status`, null, { params: { status } }),
  deleteSpill: (id) => api.delete(`/spills/${id}`),

  // Real-time updates
  subscribeToUpdates: (onUpdate) => {
    const sse = new EventSource('/api/real-time-updates');
    sse.onmessage = (event) => {
      try {
        const updates = JSON.parse(event.data);
        onUpdate(updates);
      } catch (e) {
        console.error("Error parsing SSE data", e);
      }
    };
    sse.onerror = (error) => {
      console.error("EventSource failed:", error);
    };
    console.log('Subscribing to real-time updates...');
    return () => {
      sse.close();
      console.log('Unsubscribing from real-time updates.');
    };
  },

  // Weather and Tide Data
  getWeatherData: (latitude, longitude) => api.get('/weather/current', { params: { latitude, longitude } }),
  getWeatherForecast: (latitude, longitude) => api.get('/weather/forecast', { params: { latitude, longitude, hoursAhead: 72 } }),
  getTidalData: (latitude, longitude) => api.get('/tides/forecast', { params: { latitude, longitude, hoursAhead: 72 } }), // NEW

  // Generic retry mechanism
  async retryRequest(request, maxRetries = 3, delay = 1000) {
    for (let attempt = 1; attempt <= maxRetries; attempt++) {
      try {
        return await request();
      } catch (error) {
        if (attempt === maxRetries) {
          throw error;
        }
        console.warn(`Retrying API call (${attempt}/${maxRetries}) in ${delay}ms...`);
        await new Promise(resolve => setTimeout(resolve, delay));
        delay *= 2;
      }
    }
  },

  async batchRequests(requests, batchSize = 5, delay = 100) {
    const results = [];
    for (let i = 0; i < requests.length; i += batchSize) {
      const batch = requests.slice(i, i + batchSize);
      const batchResults = await Promise.allSettled(batch);
      results.push(...batchResults);
      if (i + batchSize < requests.length) {
        await new Promise(resolve => setTimeout(resolve, delay));
      }
    }
    return results;
  },

  formatCoordinates(lat, lng, precision = 6) {
    return {
      latitude: parseFloat(lat.toFixed(precision)),
      longitude: parseFloat(lng.toFixed(precision))
    };
  },

  validateSpillData(spillData) {
    const required = ['name', 'chemicalType', 'volume', 'latitude', 'longitude', 'spillTime'];
    const errors = [];
    for (const field of required) {
      if (!spillData[field]) errors.push(`${field} is required`);
    }
    if (spillData.volume && spillData.volume <= 0) errors.push('Volume must be positive');
    if (spillData.latitude && (spillData.latitude < -90 || spillData.latitude > 90)) errors.push('Latitude must be between -90 and 90');
    if (spillData.longitude && (spillData.longitude < -180 || spillData.longitude > 180)) errors.push('Longitude must be between -180 and 180');
    return errors;
  }
};
